mapper.select:
SELECT a.*, p.name as platform_name FROM api_info a
LEFT JOIN platform p ON a.platform_id = p.id


        AND a.platform_id = #{platformId}


        AND a.method = #{method}


        AND a.name LIKE CONCAT('%', #{name}, '%')


ORDER BY a.update_time DESC
LIMIT #{offset}, #{limit}

SELECT COUNT(*) FROM api_info a


        AND a.platform_id = #{platformId}


        AND a.method = #{method}


        AND a.name LIKE CONCAT('%', #{name}, '%')

SELECT a.*, p.name as platform_name FROM api_info a
LEFT JOIN platform p ON a.platform_id = p.id
WHERE a.id = #{id}

mapper.update:
UPDATE api_info
name = #{name}, path = #{path}, method = #{method}, description = #{description}, platform_id = #{platformId}, is_aggregate = #{isAggregate}, status = #{status}, request_params = #{requestParams}, response_example = #{responseExample}, aggregate_config = #{aggregateConfig}, update_time = NOW()
WHERE id = #{id}

mapper.select:
SELECT * FROM api_info 
WHERE path = #{path} AND method = #{method}
LIMIT 1

SELECT * FROM api_info
WHERE path = #{path}
LIMIT 1

SELECT * FROM api_info WHERE is_aggregate = 1
AND name LIKE CONCAT('%', #{name}, '%')
ORDER BY update_time DESC LIMIT #{offset}, #{limit}
