mapper.select:
SELECT u.*, r.name as role_name 
FROM user u
LEFT JOIN role r ON u.role_id = r.id


        AND u.username LIKE CONCAT('%', #{params.username}, '%')


        AND u.role_id = #{params.roleId}


        AND u.status = #{params.status}


ORDER BY u.id DESC
LIMIT #{params.offset}, #{params.limit}

SELECT COUNT(*)
FROM user u


        AND u.username LIKE CONCAT('%', #{params.username}, '%')


        AND u.role_id = #{params.roleId}


        AND u.status = #{params.status}

mapper.update:
UPDATE user
real_name = #{realName}, email = #{email}, phone = #{phone}, avatar = #{avatar}, role_id = #{roleId}, status = #{status}, password = #{password},
WHERE id = #{id}
